mapper.select:
		select * from 
		(select rownum as no, a.* from wbs a
		where  manager = #{manager} 

    	and status = #{status} 


    	and title = #{title} 
		)
		where no between #{start} and #{end}

select a.*,b.compmsg from wbs a,works b
where a.workcode = #{workcode}
and a.workcode = b.workcode(+)
