mapper.select:
		select count(*) from wbs
		where manager = #{manager}

    	and status = #{status} 


    	and title like '%'||#{title}||'%'


		SELECT * from
		(SELECT rownum AS no, b.* from
		(SELECT a.*,m.name as pm_name FROM wbs a,MEMBER m 
		WHERE manager = #{manager}
		AND pm = m.id

    	and status = #{status} 


    	and title like '%'||#{title}||'%' 

		ORDER BY START_DATE DESC) b)
		WHERE no BETWEEN #{start} AND #{end}

mapper.update:
update wbs
	set status = #{status}
where workcode = #{workcode}

update works
	set compmsg = #{compmsg}
where workcode = #{workcode}

update works
	set apprmsg = #{apprmsg}
where workcode = #{workcode}
